import React, { Component } from 'react';
const fs = require('fs')
class SignUp extends Component {
    constructor(props) {
        super(props);
        this.state = { 
            Username:'',
            Password:'',
            DOB:'',
            Gender:'',
            Number:'',
            message:''
         }


    }
    user = (e)=>{
        this.setState({Username:e.target.value})
    }
    pass = (e)=>{
        this.setState({Password:e.target.value})
    }
    mno = (e)=>{
        this.setState({Number:e.target.value})
    }
    dob = (e)=>{
        this.setState({DOB:e.target.value})
    }
    pass = (e)=>{
        this.setState({Password:e.target.value})
    }
    check = () =>{
        let data =fs.readFileSync('../Data/data.json', {encoding:'utf8'} )
        if(this.state.Username.value in data ){
            this.setState({message:<p>Username already exist</p>})
        }else{
            data.Username = this.state
            fs.writeFile('../Data/data.json',data,(err)=>{
                if (err){
                    console.log(err)
                }else{
                    console.log('Done')
                }
            });
        }
        this.setState({message:<p>DONE</p>})

    }
    render() { 
        return ( 
            <div>
                <p>Username : <input value={this.state.Username.value} placeholder='Username' onChange={e=>this.user(e)} /> </p>
                <p>Password : <input value={this.state.Password} placeholder='Pxyz@123' onChange={e=>this.pass(e)} type='password' /> </p>
                <p>M.Number: <input value={this.state.Number} placeholder='xxxxxxxxxx' onChange={e=>this.mno(e)} /> </p>
                <p> DOB   : <input value={this.state.DOB} placeholder='xx/xx/20xx' onChange={e=>this.dob(e)} /> </p>

                <button onClick={e=>this.check()} >submit</button>
                {this.state.message}
            </div>
         );
    }
}

export default SignUp;
