import React from 'react';
import './App.css';
import LogIn from "./component/LogIn"


class App extends React.Component{
  constructor(){
    super()
    this.state={
      pressed:false
    }
  }
  pressing=()=>{
    this.setState({pressed:true})
  }
  renderContent = () =>{
    switch(this.state.pressed){
      case true:
        return <LogIn/>
      case false:
        return 0
    }
  }
  render(){
  return (
    <div className="App">
      <button onClick={e=>this.pressing()}>LogIn</button>
      {this.renderContent()}
    </div>
  );}
}

export default App;
