import React from 'react';
import './App.css';
import LogIn from "./component/LogIn"
import SignUp from './component/SignUp'


class App extends React.Component{
  constructor(){
    super()
    this.state={
      pressed:false
    }
  }
  pressing=()=>{
    this.setState({pressed:!this.state.pressed})
  }
  renderContent = () =>{
    switch(this.state.pressed){
      case true:
        return <LogIn/>
      case false:
        return <SignUp/>
    }
  }
  renderContentinsideButton = () =>{
    switch(this.state.pressed){
      case true:
        return "SignUp"
      case false:
        return "LogIn"
    }
  }
  render(){
  return (
    <div className="App"  >
      <button onClick={e=>this.pressing()} style={{selfAlign : "left"}}>{this.renderContentinsideButton()}</button>
      {this.renderContent()}
    </div>
  );}
}

export default App;
